using Microsoft.AspNetCore.Mvc; using GOGRelief.Data; using GOGRelief.Data.Models; using System.Threading.Tasks; namespace GOGRelief.Web.Controllers { public class DonationsController : Controller { private readonly ApplicationDbContext _db; public DonationsController(ApplicationDbContext db) { _db = db; } public IActionResult Create() => View(); [HttpPost] public async Task<IActionResult> Create(Donation model) { if (!ModelState.IsValid) return View(model); _db.Donations.Add(model); await _db.SaveChangesAsync(); return RedirectToAction("ThankYou"); } public IActionResult ThankYou() => View(); } }